<div class="head_list">	<div class="head_list_judul"><h3><?php echo $title;?></h3></div>	<div class="print-button"><a href='#' onclick="print();">Cetak</a></div></div><div id="box">	<?php		echo form_open('admin/pesan/status','id="form"');		foreach($detail_pemesan as $do):	?>	<!--</?php echo form_open("admin/batik/save","id='form_masuk'"); ?>-->	  <fieldset id="personal">		<legend> Data Pemesan</legend>		<label>ID Pemesan</label><div>: <?php echo $do->id_pms?></div><br />		<label>Nama</label><div>: <?php echo $do->nm_pms?></div><br />		<label>Alamat</label><div>: <?php echo $do->alamat?></div><br />		<label>Email</label><div>: <?php echo $do->email?></div><br />		<label>Telepon</label><div>: <?php echo $do->telp?></div>	  </fieldset>	<table width="100%">		<thead>			<tr>				<th><a href="#">No</a></th>				<th><a href="#">Foto Barang</a></th>				<th><a href="#">Kode</a></th>				<th><a href="#">Nama Barang</a></th>				<th><a href="#">Harga/Barang</a></th>				<th><a href="#">Jumlah</a></th>				<th><a href="#">Total Harga/Barang</a></th>			</tr>		</thead>		<tbody>		<?php			$total = 0; $i = 1; foreach($browse_pesan as $bo):		?>			<tr>				<td class="a-center"><?php echo $i++.'.';?></td>				<td width="100"><?php					$atrimg = array('src'=>'asset/images/barang/thumbs/'.$bo->nama_field,'width'=>100);					echo img($atrimg);				?></td>				<td><?php $kdbiaya=$bo->kd_biaya_krm; echo $bo->kd_brg;?></td>				<td><?php echo $bo->nm_brg;?></td>				<td align="right"><?php echo rupiah($bo->harga); ?></td>				<td align="center"><?php echo $bo->jum_brg;?></td>				<td align="right">					<?php						$sub_jumlah = $bo->harga * $bo->jum_brg;						echo rupiah($sub_jumlah);					?>				</td>			</tr>		<?php			$total = $total+$sub_jumlah;			$status = $bo->status;			echo form_hidden('status', $status);			echo form_hidden('no_psn', $bo->no_psn);			echo form_hidden('id_pms', $bo->id_pms);			endforeach;			$sql = "SELECT * FROM biaya_kirim WHERE kd_biaya_krm = ".$kdbiaya;			$que = mysql_query($sql);			while($dk = mysql_fetch_array($que)):		?>			<tr>				<td align="right" colspan="6"><b>Biaya Pengiriman ke <?php echo $dk['nm_kota']?></b></td>				<td align="right">		<?php				$biayakirim = $dk['biaya_krm'];				echo rupiah($biayakirim);		?>				</td>			</tr>		<?php endwhile;?>			<tr>				<td colspan='6' align="right"><b>Total Harga</b></td>				<td align="right"><?php echo '<b>'.rupiah($total+$biayakirim).'</b>';?></td>			</tr>		</tbody>	</table>	  <div align="center">	  <?php		if($status == 'sedang diproses'){			echo '<input id="button1" type="submit" value="Setujui dan Kirim Barang"/>';			echo '<input name="batalpesan" id="button2" type="submit" value="Batalkan Pemesanan"/>';		}elseif($status == 'dikirim'){			echo '<input id="button1" type="submit" value="Berhasil Dikirim dan Selesai"/>';			echo '<input name="batalpesan" id="button2" type="submit" value="Batalkan Pemesanan"/>';		}	  ?>		  </div>	<?php endforeach; ?>	</form></div>