<div id="located">	<?php		foreach($detail_respon as $dr):		echo "<div align='right'>";			if($dr->tampil == "y"){				echo anchor("admin/respon/batalkan/".$this->uri->segment(4),"batalkan");			}else{				echo anchor("admin/respon/setujui/".$this->uri->segment(4),"setujui");			}		echo "</div>";		$atr = array("id"=>"formz","class"=>"myform");		echo form_open("bukutamu/simpan",$atr);	?>	<table>		<tr>			<td>Nama</td><td>:<?php echo $dr->nama;?></td>		</tr>		<tr>			<td>Email</td><td>:<?php echo $dr->email;?></td>		</tr>		<tr>			<td>Website</td><td>:<?php echo $dr->web;?></td>		</tr>		<tr>			<td>Status</td><td>:				<?php					if($dr->tampil == "y"){						echo "Ditampilkan";					}else{						echo "<font color='red'>Tidak Ditampilkan</font>";					}				?>			</td>		</tr>		<tr>			<td>Tgl. Tulis</td><td>:<?php echo $dr->tanggal." ".$dr->jam;?></td>		</tr>		<tr>			<td>Komentar</td><td>:<?php echo $dr->komentar;?></td>		</tr>	</table>	</form>	<?php endforeach; ?></div>